INSERT INTO `titles` VALUES ('5', '新名字的故事', '小说', '9787020125265', null, '2017-04-01', '492', null, '59.00', '平装', '只有你身为女人 才会知道这些丑陋的秘密\r\n两个女人 50年的友谊和战争\r\n如何成为那个更强大的她，又不被她战胜\r\n那不勒斯四部曲NO.2\r\n《我的天才女友》后续， 意大利最神秘的作家费兰特\r\n探索青年时代的激情、困惑、挣扎、背叛和失去\r\n全球畅销近千万册 被翻译成40多种语言\r\n希拉 里·克林顿、 乔纳森·弗兰岑、詹姆斯·伍德、詹姆斯·弗兰科都是她的粉丝\r\n\r\n\r\n\r\n只有你身为女人 才会知道这些丑陋的秘密\r\n两个女人 50年的友谊和战争\r\n如何成为那个更强大的她，又不被她战胜\r\n那不勒斯四部曲NO.2\r\n《我的天才女友》后续， 意大利最神秘的作家费兰特\r\n探索青年时代的激情、困惑、挣扎、背叛和失去\r\n全球畅销近千万册 被翻译成40多种语言\r\n希拉 里·克林顿、 乔纳森·弗兰岑、詹姆斯·伍德、詹姆斯·弗兰科都是她的粉丝\r\n\r\n\r\n\r\n', '陈英', '人民文学出版社', 'image/cover/新名字的故事.jpg', null, '5', '130');
