-- ----------------------------
-- Table structure for authors
-- ----------------------------
DROP TABLE IF EXISTS `authors`;
CREATE TABLE `authors` (
  `au_id` varchar(10) DEFAULT NULL,
  `au_name` varchar(100) DEFAULT NULL,
  `nationality` varchar(10) DEFAULT NULL,
  `au_introduction` varchar(800) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of authors
-- ----------------------------

-- ----------------------------
-- Table structure for collection
-- ----------------------------
DROP TABLE IF EXISTS `collection`;
CREATE TABLE `collection` (
  `user_id` varchar(10) DEFAULT NULL,
  `title_id` varchar(6) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of collection
-- ----------------------------

-- ----------------------------
-- Table structure for comment
-- ----------------------------
DROP TABLE IF EXISTS `comment`;
CREATE TABLE `comment` (
  `content` varchar(500) DEFAULT NULL,
  `comment_date` datetime DEFAULT NULL,
  `likes` varchar(5) DEFAULT NULL,
  `title_id` varchar(6) DEFAULT NULL,
  `user_id` varchar(5) DEFAULT NULL,
  `checks` varchar(2) DEFAULT NULL,
  `comment_id` int(10) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`comment_id`)
) ENGINE=InnoDB AUTO_INCREMENT=54 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of comment
-- ----------------------------

-- ----------------------------
-- Table structure for orders
-- ----------------------------
DROP TABLE IF EXISTS `orders`;
CREATE TABLE `orders` (
  `user_id` varchar(5) DEFAULT NULL,
  `order_id` varchar(20) DEFAULT NULL,
  `order_time` varchar(20) DEFAULT NULL,
  `title_id` varchar(5) DEFAULT NULL,
  `price` varchar(5) DEFAULT NULL,
  `num` int(5) DEFAULT NULL,
  `checks` varchar(2) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of orders
-- ----------------------------

-- ----------------------------
-- Table structure for shoppingcart
-- ----------------------------
DROP TABLE IF EXISTS `shoppingcart`;
CREATE TABLE `shoppingcart` (
  `title_id` varchar(6) DEFAULT NULL,
  `user_id` varchar(5) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of shoppingcart
-- ----------------------------

-- ----------------------------
-- Table structure for titles
-- ----------------------------
DROP TABLE IF EXISTS `titles`;
CREATE TABLE `titles` (
  `title_id` varchar(6) NOT NULL,
  `title` varchar(20) DEFAULT NULL,
  `type` varchar(10) DEFAULT NULL,
  `ISBN` varchar(20) DEFAULT NULL,
  `primitive_name` varchar(50) DEFAULT NULL,
  `pub_date` date DEFAULT NULL,
  `page` int(5) DEFAULT NULL,
  `origin_price` float(5,2) DEFAULT NULL,
  `price` float(5,2) DEFAULT NULL,
  `package` varchar(2) DEFAULT NULL,
  `title_introduction` varchar(800) DEFAULT NULL,
  `translator` varchar(10) DEFAULT NULL,
  `publisher` varchar(15) DEFAULT NULL,
  `cover` varchar(30) DEFAULT NULL,
  `grade` double(10,2) DEFAULT NULL,
  `au_id` varchar(10) DEFAULT NULL,
  `sale_num` int(10) DEFAULT NULL,
  PRIMARY KEY (`title_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of titles
-- ----------------------------

-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `username` varchar(20) DEFAULT NULL,
  `password` varchar(10) DEFAULT NULL,
  `user_id` int(5) NOT NULL AUTO_INCREMENT,
  `tel` varchar(15) DEFAULT NULL,
  `email` varchar(20) DEFAULT NULL,
  `address` varchar(255) DEFAULT NULL,
  `sex` varchar(10) DEFAULT NULL,
  `birthday` varchar(10) DEFAULT NULL,
  `head_pic` varchar(50) DEFAULT NULL,
  `account_number` varchar(10) DEFAULT NULL,
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of user
-- ----------------------------
